package ChengFaBiao;

public class chengfabiao {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=9;i++)
		{
			for(int j=1;j<=i;j++){
				System.out.println(i+"*"+j+"="+(i*j)+"\t");
			}
			System.out.println(" ");
		}

	}

}
